Scripting.Code:
function m() {
	var s,editorName,editorValue;
	editorName=cp.Doc.GetText('editorName');
	editorValue=cp.Doc.GetText('editorValue');
	editorValue=cp.Utils.EncodeHTML(editorValue);
	s='<div class="codeMirrorJsEditor" style="border-top: 1px solid black; border-bottom: 1px solid black;background-color:white;"><textarea id="'+editorName+'" name="'+editorName+'" cols="120" rows="30">'+editorValue+'</text'+'area></div>';
	s+='\n<script type="text/javascript">';
	s+='\nvar editor = CodeMirror.fromTextArea("'+editorName+'", { height: "350px", parserfile: ["tokenizejavascript.js", "parsejavascript.js"], stylesheet: "/codeMirror/css/jscolors.css", path: "/codeMirror/js/", continuousScanning: 500, lineNumbers: true, autoMatchParens: true });'
	s+='\n</script>'
	return s;
}
