        padding-right: .3em ! important;
        font-size: 10pt ! important;
        font-family: monospace ! important;
        line-height: normal ! important;
      }

Scripting.Code:
function m() {
  var s,editorName,editorValue;
  editorName=cp.doc.Var('editorName');
  editorValue=cp.doc.Var('editorValue');
  editorValue=cp.utils.encodeHtml(editorValue);
  s='<div class="codeMirrorJsEditor" style="border-top: 1px solid black; border-bottom: 1px solid black;background-color:white;"><textarea id="'+editorName+'" name="'+editorName+'" cols="120" rows="30">'+editorValue+'</text'+'area></div>';
  s+='\n<script type="text/javascript">';
  s+='\nvar editor = CodeMirror.fromTextArea("'+editorName+'", { height: "350px", parserfile: "parsexml.js", stylesheet: "/codeMirror/css/xmlcolors.css", path: "/codeMirror/js/", continuousScanning: 500, lineNumbers: true, autoMatchParens: true });'
    s+='\n</script>'
return s;
}

